import Link from 'next/link';
import PropTypes from 'prop-types';
import React, { useCallback, useState } from 'react';
import { Menu, Input, Row, Col } from 'antd';
import 'antd/dist/antd.css';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import Router from 'next/router';
import UserProfile from './UserProfile';
import LoginForm from './LoginForm';
import useInput from '../hooks/useInput';

const SidebarImg = styled.img`
  width: 40px;
`;

const SidebarUl = styled.ul`
  list-style-type: none;
`;

const SidebarLi = styled.li`
  cursor: pointer;
  transition: all 0.25s ease;

  &:hover {
    transform: scale(1.3, 1.3);
  }
`;

const SidebarDiv = styled.div`
  left: 0px;
  border-radius: 2rem;
  color: black;
  width: 200px;
  height: 50px;
  font-weight: bold;
  line-height: 250%;
`;

const LeftSidebar = styled.div` 
  position: fixed;
  float: left;
  top: 10px;
  left: 300px;
  width: 400px;
  height: 1000px;
  background: #f3f5fa15;
  padding: 0px 0;
  overflow: hidden;
  color: black;
  font-size: 20px;
`;

const RightSidebar = styled.div` 
  position: fixed;
  float: left;
  top: 10px;
  right: 200px;
  background: #f3f5fa15;
  padding: 0px 0;
  overflow: hidden;
  color: black;
  font-size: 20px;
  z-index: 10;
`;

const SearchInput = styled(Input.Search)`
  vertical-align: middle;
`;

function AppLayout({ children }) {
  const [searchInput, onChangeSearchInput] = useInput('');
  const { me } = useSelector((state) => state.user);

  const onSearch = useCallback(() => {
    Router.push(`/hashtag/${searchInput}`);
  }, [searchInput]);

  // const const {isLoggedIn} = useSelector((state)=>state.user);
  return (
    <div>
      <div>
        <div style={{ position: 'fixed', left: 560, top: 0, zIndex: 6, width: 1500, overflow: 'hidden' }}>
          <Menu mode="horizontal">
            <Menu.Item>
              <Link href="/">
                <a><h2>홈</h2></a>
              </Link>
            </Menu.Item>
            <Menu.Item>
              <Link href="/profile">
                <a>프로필</a>
              </Link>
            </Menu.Item>
            {/* <Menu.Item> */}
            {/*  <Link href="/signup"> */}
            {/*    <a>회원가입</a> */}
            {/*  </Link> */}
            {/* </Menu.Item> */}
          </Menu>
        </div>
        <div>
          <LeftSidebar>
            <SidebarDiv>
              <SidebarUl>
                  <Link href="/">
                <SidebarLi>
                    <SidebarImg src="favicon.png" />
                </SidebarLi>
                  </Link>
                <SidebarLi>
                  <Link href="/">
                    <SidebarImg src="favicon.png" />
                  </Link>
                </SidebarLi>
                <SidebarLi>
                  <Link href="/">
                    <SidebarImg src="favicon.png" />
                  </Link>
                </SidebarLi>
                <SidebarLi>
                  <Link href="/">
                    <SidebarImg src="favicon.png" />
                  </Link>
                </SidebarLi>
                <SidebarLi>
                  <Link href="/">
                    <SidebarImg src="favicon.png" />
                  </Link>
                </SidebarLi>
                <SidebarLi>
                  <Link href="/">
                    <SidebarImg src="favicon.png" />
                  </Link>
                </SidebarLi>
                <SidebarLi>
                  <Link href="/">
                    <SidebarImg src="favicon.png" />
                  </Link>
                </SidebarLi>
                <SidebarLi>
                  <Link href="/">
                    <SidebarImg src="favicon.png" />
                  </Link>
                </SidebarLi>
                <SidebarLi>
                  <Link href="/">
                    <SidebarImg src="favicon.png" />
                  </Link>
                </SidebarLi>
              </SidebarUl>
            </SidebarDiv>
          </LeftSidebar>
        </div>
        <div>
          <RightSidebar>
            <SearchInput
              enterButton
              value={searchInput}
              onChange={onChangeSearchInput}
              onSearch={onSearch}
            />
          </RightSidebar>
        </div>
      </div>
      <div style={{ marginTop: 100, zIndex: 5 }}>
        <Row gutter={8}>
          <Col xs={24} md={7} />
          <Col xs={24} md={11}>
            {children}
          </Col>
          <Col xs={24} md={6} />
        </Row>
      </div>
    </div>
  );
}

AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
};

export default AppLayout;
