import Link from 'next/link';
import PropTypes from 'prop-types';
import React, { useCallback, useState } from 'react';
import { Menu, Input, Row, Col } from 'antd';
import 'antd/dist/antd.css';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import Router from 'next/router';
import UserProfile from './UserProfile';
import LoginForm from './LoginForm';
import useInput from '../hooks/useInput';

const NavMenu = styled.div`
  left: 0px;
  background-color: skyblue;
  border-radius: 2rem;
  text-align: center;
  color: white;
  width: 200px;
  height: 50px;
  font-weight: bold;
  line-height: 250%;
`;

const LeftSidebar = styled.div` 
  position: fixed;
  float: left;
  top: 46.5px;
  left: 80px;
  width: 200px;
  height: 1000px;
  background: #f3f5fa15;
  padding: 40px 0;
  overflow: hidden;
  color: black;
  font-size: 20px;
`;

const SearchInput = styled(Input.Search)`
  vertical-align: middle;
`;

function AppLayout({ children }) {
  const [searchInput, onChangeSearchInput] = useInput('');
  const { me } = useSelector((state) => state.user);

  const onSearch = useCallback(() => {
    Router.push(`/hashtag/${searchInput}`);
  }, [searchInput]);

  // const const {isLoggedIn} = useSelector((state)=>state.user);
  return (
    <div>
      <div>
        <div style={{ position: 'fixed', left: 288, top: 0, zIndex: 6, width: 1300 }}>
          <Menu mode="horizontal">
            <Menu.Item>
              <Link href="/">
                <a>노드버드</a>
              </Link>
            </Menu.Item>
            <Menu.Item>
              <Link href="/profile">
                <a>프로필</a>
              </Link>
            </Menu.Item>
            <Menu.Item>
              <SearchInput
                enterButton
                value={searchInput}
                onChange={onChangeSearchInput}
                onSearch={onSearch}
              />
            </Menu.Item>
            {/* <Menu.Item> */}
            {/*  <Link href="/signup"> */}
            {/*    <a>회원가입</a> */}
            {/*  </Link> */}
            {/* </Menu.Item> */}
          </Menu>
        </div>
        <div>
          <LeftSidebar>
            <img src="favicon.png" style={{ width: 40 }} />
            <br />
            <br />
            <NavMenu>
              버튼1
            </NavMenu>
            <br />
            <NavMenu>
              버튼1
            </NavMenu>
            <br />
            <NavMenu>
              버튼1
            </NavMenu>
            <br />
            <NavMenu>
              버튼1
            </NavMenu>
          </LeftSidebar>
        </div>
      </div>
      <div style={{ marginTop: 100, zIndex: 5 }}>
        <Row gutter={8}>
          <Col xs={24} md={6} />
          <Col xs={24} md={12}>
            {children}
          </Col>
          <Col xs={24} md={6}>
            <a
              href="https://github.com/TheON2"
              target="_blank"
              rel="noreferrer noopener"
            >
              Made by TheON2
            </a>
          </Col>
        </Row>
      </div>
    </div>
  );
}

AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
};

export default AppLayout;
