import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useInView } from 'react-intersection-observer';
import axios from 'axios';
import { END } from 'redux-saga';
import AppLayout from '../components/AppLayout';
import PostForm from '../components/PostForm';
import PostCard from '../components/PostCard';
import { LOAD_POST_REQUEST } from '../reducers/post';
import { LOAD_MY_INFO_REQUEST } from '../reducers/user';
import wrapper from '../store/configureStore';
import LoginForm from '../components/LoginForm';
import UserProfile from '../components/UserProfile';
import {Col, Row} from "antd";

function Home() {
  const dispatch = useDispatch();
  const { me } = useSelector((state) => state.user);
  const { mainPosts, hasMorePost, loadPostLoading, reTweetError, id } = useSelector((state) => state.post);

  useEffect(() => {
    if (reTweetError) {
      alert(reTweetError);
    }
  }, [reTweetError]);

  // useEffect(() => {
  //   dispatch({
  //     type: LOAD_MY_INFO_REQUEST,
  //   });
  // }, []);
  //
  // useEffect(() => {
  //   dispatch({
  //     type: LOAD_POST_REQUEST,
  //   });
  // }, []);

  const [ref, inView] = useInView();

  useEffect(
    () => {
      if (inView && hasMorePost && !loadPostLoading) {
        const lastId = mainPosts[mainPosts.length - 1]?.id;
        dispatch({
          type: LOAD_POST_REQUEST,
          lastId,
        });
      }
    },
    [inView, hasMorePost, loadPostLoading, mainPosts, id],
  );

  return (

    <div>{me ? (
      <AppLayout>
        {me && <PostForm />}
        {mainPosts.map((post) => <PostCard key={post.id} post={post} />)}
        <div ref={hasMorePost && !loadPostLoading ? ref : undefined} />
      </AppLayout>

    ) : (
      <Row gutter={8}>
        <Col xs={24} md={7}>
          <img src="main.png"/>
        </Col>
        <Col xs={24} md={17}>
          <LoginForm />
        </Col>
        <Col xs={24} md={0}>
          <a
            href="https://github.com/TheON2"
            target="_blank"
            rel="noreferrer noopener"
          >
            Made by TheON2
          </a>
        </Col>
      </Row>
    )}
    </div>
  );
}

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
  const cookie = context.req ? context.req.headers.cookie : '';
  axios.defaults.headers.Cookie = cookie;
  axios.defaults.headers.Cookie = '';
  if (context.req && cookie) { // 타 유저간 쿠키가 공유되는 문제를 방지하기 위함
    axios.defaults.headers.Cookie = cookie;
  }

  context.store.dispatch({
    type: LOAD_MY_INFO_REQUEST,
  });
  context.store.dispatch({
    type: LOAD_POST_REQUEST,
  });
  context.store.dispatch(END);
  await context.store.sagaTask.toPromise();
});

export default Home;
