import React, { useCallback, useState } from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { Button, Input } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { MODIFY_POST_REQUEST } from '../reducers/post';

const { TextArea } = Input;

function PostCardContent({ postData, editMode, onCancelUpdate, onChangePost }) {
  const dispatch = useDispatch();
  const [editText, setEditText] = useState(postData);
  const { modifyPostLoading } = useSelector((state) => state.post);

  const onChangeText = useCallback((e) => {
    setEditText(e.target.value);
  }, []);

  return (
    <div>
      {editMode
        ? (
          <>
            <TextArea value={editText} onchange={onChangeText} />
            <Button.Group>
              <Button loading={modifyPostLoading} onClick={onChangePost(editText)}>수정</Button>}
              <Button type="danger" onClick={onCancelUpdatePost}>취소</Button>
            </Button.Group>
          </>
        )
        : postData.split(/(#[^\s#]+)/g).map((v, i) => {
          if (v.match(/(#[^\s#]+)/)) {
            return <Link href={`/hashtag/${v.slice(1)}`} key={i}><a>{v}</a></Link>;
          }
          return v;
        })}
    </div>
  );
}

PostCardContent.propTypes = { postData: PropTypes.string.isRequired,
  editMode: PropTypes.bool,
  onCancelUpdate: PropTypes.func.isRequired,
  onChangePost: PropTypes.func.isRequired,
};

PostCardContent.defaultProps = {
  editMode: false,
};

export default PostCardContent;
